import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

const titles = ["Breaking bad", "Narcos", "Game of Thrones"];

const MovieListItem = ({ title }) => (
  <li>{title}</li>
)

//instead of (props) we can do ({ titles })
const MovieList = ({ titles }) => (
  <ul>
    {
      //titles is the array, title is the element we are currently looking at inside the array
      props.titles.map( (title, index) => 
        <MovieListItem key={index.toString()} title={title}/>
        )
    }
  </ul>
)

class App extends Component {
  render() {
    return (
      <div className="App">
        <MovieList titles={titles} />
      </div>
    );
  }
}

export default App;
